package may;

public class Logicalopraters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int	a=8, b=25, c=19;
	
	// logical and (&&)
	
	boolean d = a!=c && c<=b ; 
	
	System.out.println (d);
	
	// logical or (||)
	d = 42>b || b<=c;
	
	System.out.println (d);
	
	}

}
